import React, { useState, useEffect, useCallback, useRef } from 'react';
import { 
  Card, 
  Typography, 
  Input, 
  Button, 
  List, 
  Avatar, 
  Space,
  Divider,
  Badge,
  Row,
  Col,
  Tooltip
} from 'antd';
import { 
  SendOutlined, 
  UserOutlined,
  MessageOutlined 
} from '@ant-design/icons';
import { useAuth } from '../../contexts/AuthContext';
import socketService from '../../services/socketService';
import { chatService, Message, ChatUser } from '../../services/chatService';
import './MessengerChatPage.css';

const { Title, Text } = Typography;
const { TextArea } = Input;

const MessengerChatPage: React.FC = () => {
  const { user, token } = useAuth();
  const [message, setMessage] = useState('');
  const [messagesByConversation, setMessagesByConversation] = useState<{ [key: string]: Message[] }>({});
  const [activeUsers, setActiveUsers] = useState<ChatUser[]>([]);
  const [selectedUser, setSelectedUser] = useState<ChatUser | null>(null);
  const [typing, setTyping] = useState<{ [key: string]: boolean }>({});
  const typingTimeoutRef = useRef<{ [key: string]: NodeJS.Timeout }>({});
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const [socketConnected, setSocketConnected] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };
  const loadMessages = useCallback(async (userId: string) => {
    try {
      console.log('ðŸ”„ Loading messages for user:', userId);
      const messages = await chatService.getMessages(userId);
      console.log('ðŸ“¨ Received messages from API:', {
        userId,
        count: messages.length,
        messages: messages.map(m => ({
          id: m._id,
          content: m.content.substring(0, 50) + (m.content.length > 50 ? '...' : ''),
          timestamp: m.timestamp,
          sender: typeof m.sender === 'string' ? m.sender : m.sender._id,
          receiver: typeof m.receiver === 'string' ? m.receiver : m.receiver._id
        }))
      });
      
      // Sort messages by timestamp
      const sortedMessages = messages.sort((a, b) => 
        new Date(a.timestamp).getTime() - new Date(b.timestamp).getTime()
      );
      
      setMessagesByConversation(prev => ({
        ...prev,
        [userId]: sortedMessages
      }));
      
      console.log('âœ… Messages loaded and set in state for user:', userId);
      scrollToBottom();
      
      // Mark messages as read
      chatService.markAsRead(userId)
        .catch(error => console.error('Error marking messages as read:', error));
    } catch (error) {
      console.error('âŒ Error loading messages:', error);
    }
  }, []);const loadActiveUsers = useCallback(async () => {
    try {
      const users = await chatService.getActiveUsers();
      // Filter out current user
      const filteredUsers = users.filter(u => u._id !== user?._id);
      setActiveUsers(filteredUsers);
      
      // Auto-select first user if no user is selected and there are users available
      if (!selectedUser && filteredUsers.length > 0) {
        setSelectedUser(filteredUsers[0]);
      }
    } catch (error) {
      console.error('Error loading active users:', error);
    }
  }, [user?._id, selectedUser]);  useEffect(() => {
    if (token && !socketConnected) {
      console.log('ðŸ”Œ Connecting socket with token');
      const socket = socketService.connect(`Bearer ${token}`);
      
      if (socket) {
        socket.on('connect', () => {
          console.log('âœ… Socket connected successfully');
          setSocketConnected(true);
          // Reload data when connected
          if (selectedUser) {
            loadMessages(selectedUser._id);
          }
          loadActiveUsers();
        });

        socket.on('reconnect', () => {
          console.log('ðŸ”„ Socket reconnected successfully');
          setSocketConnected(true);
          // Reload data when reconnected
          if (selectedUser) {
            loadMessages(selectedUser._id);
          }
          loadActiveUsers();
        });

        socket.on('connect_error', (error: Error) => {
          console.error('âŒ Socket connection error:', error);
          setSocketConnected(false);
        });

        socket.on('disconnect', (reason) => {
          console.log('ðŸ”Œ Socket disconnected:', reason);
          // Only set disconnected for permanent disconnections
          if (reason === 'io server disconnect' || reason === 'io client disconnect') {
            setSocketConnected(false);
          }
          // For temporary disconnections, keep the connected state and let reconnect handle it
        });
      }

      loadActiveUsers();

      return () => {
        console.log('ðŸ§¹ Cleaning up socket connection');
        if (socketService.isConnected()) {
          socketService.disconnect();
        }
        setSocketConnected(false);
      };
    }
  }, [token, loadActiveUsers, selectedUser, loadMessages]); // Removed socketConnected from dependencies

  // Handle token updates - ensure socket service is updated with new tokens
  useEffect(() => {
    if (token && socketService.isConnected()) {
      console.log('ðŸ”‘ Token updated, updating socket service');
      socketService.updateToken(`Bearer ${token}`);
    }
  }, [token]);

  useEffect(() => {
    if (socketConnected) {
      const messageHandler = (newMessage: Message) => {
        console.log('ðŸ”„ Received message via socket:', {
          messageId: newMessage._id,
          sender: newMessage.sender,
          receiver: newMessage.receiver,
          content: newMessage.content?.substring(0, 50) + (newMessage.content?.length > 50 ? '...' : ''),
          timestamp: newMessage.timestamp,
          createdAt: newMessage.createdAt
        });

        // Extract IDs correctly regardless of the format
        const senderId = typeof newMessage.sender === 'string' 
          ? newMessage.sender 
          : newMessage.sender._id;
        
        const receiverId = typeof newMessage.receiver === 'string'
          ? newMessage.receiver 
          : newMessage.receiver._id;
        
        const userId = String(user?._id);
        const isMessageForCurrentUser = senderId === userId || receiverId === userId;

        console.log('ðŸ” Message routing info:', {
          senderId,
          receiverId,
          currentUserId: userId,
          isForCurrentUser: isMessageForCurrentUser,
          isSender: senderId === userId,
          isReceiver: receiverId === userId
        });

        // Only update conversation if we're either the sender or receiver
        if (isMessageForCurrentUser) {
          // Get the ID of the other person in the conversation
          const conversationId = senderId === userId ? receiverId : senderId;
          console.log('ðŸ“‹ Updating conversation:', conversationId);

          setMessagesByConversation(prev => {
            const conversationMessages = prev[conversationId] || [];
            console.log('ðŸ“‹ Current conversation messages count:', conversationMessages.length);
            
            // Check if this is confirming an optimistic message (for sender)
            const optimisticIndex = conversationMessages.findIndex(msg => 
              msg._id.startsWith('temp_') && 
              msg.content === newMessage.content && 
              senderId === userId &&
              Math.abs(new Date(msg.timestamp).getTime() - new Date(newMessage.timestamp || newMessage.createdAt || newMessage.timestamp).getTime()) < 10000 // Within 10 seconds
            );

            if (optimisticIndex !== -1) {
              // Replace optimistic message with real message
              const updatedMessages = [...conversationMessages];
              const normalizedMessage = {
                ...newMessage,
                sender: senderId,
                receiver: receiverId,
                _id: String(newMessage._id),
                timestamp: newMessage.timestamp || newMessage.createdAt || new Date()
              };
              updatedMessages[optimisticIndex] = normalizedMessage;
              
              console.log('âœ… Replaced optimistic message with real message:', {
                tempId: conversationMessages[optimisticIndex]._id,
                realId: newMessage._id,
                timestamp: normalizedMessage.timestamp
              });
              
              return {
                ...prev,
                [conversationId]: updatedMessages.sort((a, b) => 
                  new Date(a.timestamp).getTime() - new Date(b.timestamp).getTime()
                )
              };
            }

            // Check if message already exists by ID (prevent duplicates)
            const exists = conversationMessages.some(msg => 
              String(msg._id) === String(newMessage._id)
            );
            
            if (!exists) {
              // Add new message (for receiver or if no optimistic message found)
              const normalizedMessage = {
                ...newMessage,
                sender: senderId,
                receiver: receiverId,
                _id: String(newMessage._id),
                timestamp: newMessage.timestamp || newMessage.createdAt || new Date()
              };

              console.log('âž• Adding new message to conversation:', {
                conversationId,
                messageId: normalizedMessage._id,
                timestamp: normalizedMessage.timestamp,
                isFromSender: senderId === userId
              });
              
              // Sort messages by timestamp
              const updatedMessages = [...conversationMessages, normalizedMessage]
                .sort((a, b) => new Date(a.timestamp).getTime() - new Date(b.timestamp).getTime());
              
              return {
                ...prev,
                [conversationId]: updatedMessages
              };
            }
            
            console.log('âš ï¸ Message already exists, not adding duplicate:', newMessage._id);
            return prev;
          });
        }

        // Only scroll if the message belongs to the currently selected conversation
        if (selectedUser && (
          senderId === String(selectedUser._id) || 
          receiverId === String(selectedUser._id)
        )) {
          console.log('ðŸ“œ Scrolling to bottom for message in active conversation');
          scrollToBottom(); // Instant scrolling for real-time feel
        }
      };

      // Handle database save confirmation
      const messageDbSavedHandler = ({ tempId, realId }: { tempId: string, realId: string }) => {
        console.log('ðŸ’¾ Database save confirmed:', { tempId, realId });
        
        setMessagesByConversation(prev => {
          const updatedConversations = { ...prev };
          
          // Find and update the message with temp ID across all conversations
          Object.keys(updatedConversations).forEach(conversationId => {
            const messages = updatedConversations[conversationId];
            const messageIndex = messages.findIndex(msg => msg._id === tempId);
            
            if (messageIndex !== -1) {
              const updatedMessages = [...messages];
              updatedMessages[messageIndex] = {
                ...updatedMessages[messageIndex],
                _id: realId
              };
              updatedConversations[conversationId] = updatedMessages;
              console.log('ðŸ”„ Updated message ID:', { tempId, realId, conversationId });
            }
          });
          
          return updatedConversations;
        });
      };

      // Handle message errors
      const messageErrorHandler = ({ error, tempId }: { error: string, tempId?: string }) => {
        console.error('âŒ Message error:', error, tempId);
        
        if (tempId) {
          // Mark the temporary message as failed
          setMessagesByConversation(prev => {
            const updatedConversations = { ...prev };
            
            Object.keys(updatedConversations).forEach(conversationId => {
              const messages = updatedConversations[conversationId];
              const messageIndex = messages.findIndex(msg => msg._id === tempId);
              
              if (messageIndex !== -1) {
                const updatedMessages = [...messages];
                updatedMessages[messageIndex] = {
                  ...updatedMessages[messageIndex],
                  error: true,
                  errorMessage: error
                };
                updatedConversations[conversationId] = updatedMessages;
                console.log('âŒ Marked message as failed:', { tempId, error });
              }
            });
            
            return updatedConversations;
          });
        }
      };

      const userOnlineHandler = (userId: string) => {
        console.log('User came online:', userId);
        setActiveUsers(prev => 
          prev.map(user => 
            user._id === userId ? { ...user, isOnline: true } : user
          )
        );
      };

      const userOfflineHandler = (userId: string) => {
        console.log('User went offline:', userId);
        setActiveUsers(prev => 
          prev.map(user => 
            user._id === userId ? { ...user, isOnline: false } : user
          )
        );
      };

      const typingHandler = ({ userId, isTyping }: { userId: string, isTyping: boolean }) => {
        setTyping(prev => ({ ...prev, [userId]: isTyping }));
      };

      socketService.onMessage(messageHandler);
      socketService.onUserOnline(userOnlineHandler);
      socketService.onUserOffline(userOfflineHandler);
      socketService.onTyping(typingHandler);

      // Handle message confirmation for sender - use the same messageHandler
      const messageConfirmedHandler = (confirmedMessage: Message) => {
        console.log('ðŸ“§ Message confirmed event received:', {
          messageId: confirmedMessage._id,
          content: confirmedMessage.content?.substring(0, 50) + (confirmedMessage.content?.length > 50 ? '...' : ''),
          timestamp: confirmedMessage.timestamp,
          createdAt: confirmedMessage.createdAt
        });
        // Reuse the same messageHandler logic
        messageHandler(confirmedMessage);
      };

      // Listen for all message-related events
      if (socketService.getSocket()) {
        socketService.getSocket()?.on('messageConfirmed', messageConfirmedHandler);
        socketService.getSocket()?.on('messageDbSaved', messageDbSavedHandler);
        socketService.getSocket()?.on('messageError', messageErrorHandler);
      }

      return () => {
        socketService.off('newMessage');
        socketService.off('messageConfirmed');
        socketService.off('userOnline');
        socketService.off('userOffline');
        socketService.off('userTyping');
        if (socketService.getSocket()) {
          socketService.getSocket()?.off('messageConfirmed', messageConfirmedHandler);
          socketService.getSocket()?.off('messageDbSaved', messageDbSavedHandler);
          socketService.getSocket()?.off('messageError', messageErrorHandler);
        }
      };
    }
  }, [socketConnected, user, selectedUser]);  useEffect(() => {
    if (selectedUser) {
      console.log('ðŸ‘¤ Selected user changed, loading messages for:', selectedUser._id, selectedUser.username);
      loadMessages(selectedUser._id);
    } else {
      console.log('ðŸ‘¤ No user selected');
    }
  }, [selectedUser, loadMessages]);

  // Handle page visibility to maintain socket connection when tab is active
  useEffect(() => {
    const handleVisibilityChange = () => {
      if (document.hidden) {
        console.log('ðŸ“± Page hidden - socket will continue running');
      } else {
        console.log('ðŸ“± Page visible - ensuring socket connection');
        if (token && !socketService.isConnected()) {
          console.log('ðŸ”„ Page visible but socket disconnected, reconnecting...');
          socketService.connect(`Bearer ${token}`);
        }
      }
    };

    document.addEventListener('visibilitychange', handleVisibilityChange);
    
    return () => {
      document.removeEventListener('visibilitychange', handleVisibilityChange);
    };
  }, [token]);

  // Handle page visibility changes (tab switching)
  useEffect(() => {
    const handleVisibilityChange = () => {
      if (document.visibilityState === 'visible') {
        console.log('Page became visible, checking socket connection...');
        
        // Reconnect socket if needed
        if (!socketService.isConnected() && token) {
          console.log('Reconnecting socket after tab switch...');
          const socket = socketService.connect(`Bearer ${token}`);
          if (socket) {
            socket.on('connect', () => {
