import axios from 'axios';
import { LoginFormData, RegisterFormData } from '../types';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000';

// Create axios instance
const api = axios.create({
  baseURL: API_BASE_URL,
  withCredentials: true,
});

// Add auth token to requests
api.interceptors.request.use((config) => {
  const token = localStorage.getItem('token');
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

// Handle token expiration with auto-refresh
api.interceptors.response.use(
  (response) => response,
  async (error) => {
    const originalRequest = error.config;
    
    if (error.response?.status === 401 && !originalRequest._retry) {
      originalRequest._retry = true;
      
      try {
        console.log('ðŸ”„ Token expired, attempting refresh...');
        const response = await authAPI.refreshToken();
        const newToken = response.accessToken;
        
        localStorage.setItem('token', newToken);
        originalRequest.headers.Authorization = `Bearer ${newToken}`;
        
        console.log('âœ… Token refreshed successfully');
        return api(originalRequest);
      } catch (refreshError) {
        console.error('âŒ Token refresh failed:', refreshError);
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        window.location.href = '/login';
        return Promise.reject(refreshError);
      }
    }
    
    return Promise.reject(error);
  }
);

export const authAPI = {
  login: async (data: LoginFormData) => {
    const response = await api.post('/v1/auth/login', data);
    return response.data;
  },

  register: async (data: RegisterFormData) => {
    const response = await api.post('/v1/auth/register', data);
    return response.data;
  },

  logout: async () => {
    const response = await api.post('/v1/auth/logout');
    return response.data;
  },

  refreshToken: async () => {
    const response = await api.post('/v1/auth/refresh');
    return response.data;
  },

  heartbeat: async () => {
    const response = await api.post('/v1/auth/heartbeat');
    return response.data;
  }
};

export const userAPI = {
  getAllUsers: async () => {
    const response = await api.get('/v1/user');
    return response.data;
  },

  getCurrentUser: async () => {
    const response = await api.get('/v1/user/me');
    return response.data;
  },

  updateProfile: async (data: any) => {
    const response = await api.put('/v1/user/profile', data);
    return response.data;
  },
  
  updateUserByAdmin: async (id: string, data: any) => {
    const response = await api.put(`/v1/user/${id}`, data);
    return response.data;
  },

  deleteUser: async (id: string) => {
    const response = await api.delete(`/v1/user/${id}`);
    return response.data;
  }
};

export default api;
