        if (error.message.includes('Authentication error') || error.message.includes('Invalid token')) {
          console.log('ðŸ”‘ Authentication error detected, attempting token refresh...');
          this.handleAuthError();
        }
      });

      this.socket.on('disconnect', (reason) => {
        console.log('ðŸ”Œ Socket disconnected:', reason);
        this.stopHeartbeat();
        
        // Handle different disconnect reasons
        if (reason === 'io server disconnect' || reason === 'io client disconnect') {
          console.log('âš ï¸ Permanent disconnect detected');
        } else {
          console.log('ðŸ”„ Temporary disconnect, will attempt reconnect...');
          this.startReconnectAttempts();
        }
      });

      this.socket.on('reconnect_failed', () => {
        console.error('âŒ Socket failed to reconnect after all attempts');
        this.startReconnectAttempts();
      });

      this.socket.on('reconnect_error', (error) => {
        console.error('âŒ Socket reconnection error:', error);
        
        if (error.message.includes('Authentication error') || error.message.includes('Invalid token')) {
          this.handleAuthError();
        }
      });      // Message events vá»›i tá»‘i Æ°u hiá»‡u suáº¥t
      this.socket.on('newMessage', (message: Message) => {
        console.log('ðŸ“¨ Received new message:', message);
