          // Don't call message callbacks for optimized confirmations as they don't contain full message data
        } else if (confirmation._id && confirmation.sender && confirmation.receiver) {
          // Legacy format - treat as message only if it has required properties
          this.messageCallbacks.forEach(callback => callback(confirmation));
        } else {
          console.warn('âš ï¸ Received messageConfirmed with incomplete data:', confirmation);
        }
      });

      // Re-auth events (ðŸ” Re-auth qua sá»± kiá»‡n socket)
      this.socket.on('reauth_success', (data) => {
        console.log('âœ… Re-authentication successful:', data);
      });

      this.socket.on('reauth_error', (error) => {
        console.error('âŒ Re-authentication failed:', error);
        this.handleAuthError();
      });

      this.socket.on('messageError', (error: { error: string }) => {
        console.error('âŒ Message error:', error);
      });

      // User status events
      this.socket.on('userOnline', (userId: string) => {
        this.userOnlineCallbacks.forEach(callback => callback(userId));
      });

      this.socket.on('userOffline', (userId: string) => {
        this.userOfflineCallbacks.forEach(callback => callback(userId));
      });

      // Typing events
      this.socket.on('userTyping', (data: { userId: string, isTyping: boolean }) => {
        this.typingCallbacks.forEach(callback => callback(data));
      });
    }
    return this.socket;
  }

  disconnect() {
    this.stopHeartbeat();
    this.clearReconnectInterval();
    if (this.socket) {
      this.socket.removeAllListeners();
      this.socket.disconnect();
      this.socket = null;
    }
  }

  forceReconnect(token: string) {
    console.log('ðŸ”„ Force reconnecting socket...');
    this.disconnect();
    return this.connect(token);
  }

  isConnected() {
    return this.socket && this.socket.connected;
  }

  private startHeartbeat() {
    this.stopHeartbeat();
    // Send heartbeat every 15 seconds to keep connection alive
    this.heartbeatInterval = setInterval(() => {
      if (this.socket && this.socket.connected) {
        this.socket.emit('heartbeat');
        console.log('ðŸ’“ Heartbeat sent');
      }
    }, 15000);
  }

  private stopHeartbeat() {
    if (this.heartbeatInterval) {
      clearInterval(this.heartbeatInterval);
      this.heartbeatInterval = null;
    }
  }

  private clearReconnectInterval() {
    if (this.reconnectInterval) {
      clearInterval(this.reconnectInterval);
      this.reconnectInterval = null;
    }
  }

  private startReconnectAttempts() {
    if (this.reconnectInterval) return; // Already trying to reconnect

    this.reconnectInterval = setInterval(async () => {
      if (this.reconnectAttempts >= this.maxReconnectAttempts) {
        console.log('âš ï¸ Max reconnect attempts reached, trying token refresh...');
        await this.handleAuthError();
        return;
      }

      if (!this.isConnected() && this.currentToken) {
        console.log(`ðŸ”„ Manual reconnect attempt ${this.reconnectAttempts + 1}/${this.maxReconnectAttempts}`);
        this.reconnectAttempts++;
        this.forceReconnect(this.currentToken);
      } else if (this.isConnected()) {
        console.log('âœ… Socket reconnected successfully');
        this.clearReconnectInterval();
        this.reconnectAttempts = 0;
      }
    }, 5000);
  }

  private async handleAuthError() {
    try {
      console.log('ðŸ”‘ Attempting to refresh token...');
      
      // Try to refresh token using localStorage directly
      const response = await fetch(`${process.env.REACT_APP_API_URL || 'http://localhost:8000'}/v1/auth/refresh`, {
        method: 'POST',
        credentials: 'include',
        headers: {
          'Content-Type': 'application/json'
        }
      });

