  updateToken(newToken: string) {
    if (this.currentToken !== newToken) {
      console.log('ðŸ”‘ Updating socket token...');
      this.currentToken = newToken;
      
      if (this.isConnected()) {
        // Use re-auth instead of reconnect for better performance (ðŸ” Re-auth qua sá»± kiá»‡n socket)
        this.reauth(newToken);
      }
    }
  }

  // Re-authenticate with new token without disconnecting (â±ï¸ Token gáº§n háº¿t háº¡n thÃ¬ reconnect)
  reauth(newToken: string) {
    if (this.socket && this.socket.connected) {
      console.log('ðŸ”„ Re-authenticating with new token...');
      this.socket.emit('reauth', { token: newToken });
    } else {
      console.log('ðŸ”„ Socket not connected, forcing reconnect...');
      this.forceReconnect(newToken);
    }
  }

  sendMessage(receiverId: string, content: string, messageType: string = 'text') {
    if (this.socket && this.socket.connected) {
      // Create a unique key for this message to prevent duplicates
      const messageKey = `${receiverId}-${content}-${Date.now()}`;
      
      if (this.pendingMessages.has(messageKey)) {
