import React, { createContext, useContext, useState, useEffect, useCallback, ReactNode } from 'react';
import { User, AuthContextType } from '../types';
import { userAPI, authAPI } from '../services/api';
import socketService from '../services/socketService';

const AuthContext = createContext<AuthContextType | undefined>(undefined);

interface AuthProviderProps {
  children: ReactNode;
}

export const AuthProvider: React.FC<AuthProviderProps> = ({ children }) => {
  const [user, setUser] = useState<User | null>(null);
  const [token, setToken] = useState<string | null>(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  useEffect(() => {
    // Check if user is logged in on app start
    const savedUser = localStorage.getItem('user');
    const savedToken = localStorage.getItem('token');
    
    if (savedUser && savedToken) {
      setUser(JSON.parse(savedUser));
      setToken(savedToken);
      setIsAuthenticated(true);
      
      // Update socket service with existing token
      socketService.updateToken(savedToken);
    }
  }, []);
  const login = (userData: User, accessToken: string) => {
    setUser(userData);
    setToken(accessToken);
    setIsAuthenticated(true);
    localStorage.setItem('user', JSON.stringify(userData));
    localStorage.setItem('token', accessToken);
    
    // Update socket service with new token
    socketService.updateToken(accessToken);
  };  const logout = () => {
    setUser(null);
    setToken(null);
    setIsAuthenticated(false);
    localStorage.removeItem('user');
    localStorage.removeItem('token');
    
    // Disconnect socket service
    socketService.disconnect();
  };
  const updateUser = (updatedUserData: Partial<User>) => {
    if (user) {
      const newUser = { ...user, ...updatedUserData };
      setUser(newUser);
      localStorage.setItem('user', JSON.stringify(newUser));
    }
  };  const refreshUserData = useCallback(async () => {
    if (user && token) {
      try {
        // Send heartbeat to keep user online
        await authAPI.heartbeat();
        
        const userData = await userAPI.getCurrentUser();
        setUser(userData);
        localStorage.setItem('user', JSON.stringify(userData));
      } catch (error) {
        console.error('Failed to refresh user data:', error);
        // If heartbeat fails, user might be offline - don't force logout yet
      }
    }
  }, [user, token]);  // Auto-refresh token before it expires
  const refreshTokenPeriodically = useCallback(async () => {
    if (token) {
      try {
        const response = await authAPI.refreshToken();
        const newToken = response.accessToken;
        setToken(newToken);
        localStorage.setItem('token', newToken);
        
        // Update socket service with new token
        socketService.updateToken(newToken);
        
        console.log('ðŸ”„ Token auto-refreshed and socket updated');
      } catch (error) {
        console.error('âŒ Failed to refresh token:', error);
        logout();
      }
    }
  }, [token]);  // Check for user data updates and send heartbeat every 30 seconds
  useEffect(() => {
    if (isAuthenticated && user) {
      const interval = setInterval(() => {
        refreshUserData();
      }, 30000); // Check every 30 seconds

      return () => clearInterval(interval);
    }
  }, [isAuthenticated, user, refreshUserData]); // Added user to dependencies
  // Auto-refresh token every 20 hours (before it expires)
  useEffect(() => {
    if (isAuthenticated && token) {
      const interval = setInterval(() => {
        refreshTokenPeriodically();
      }, 20 * 60 * 60 * 1000); // Refresh every 20 hours

      return () => clearInterval(interval);
    }
  }, [isAuthenticated, token, refreshTokenPeriodically]);

  const value: AuthContextType = {
    user,
    isAuthenticated,
    login,
    logout,
    updateUser,
    token
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = (): AuthContextType => {
  const context = useContext(AuthContext);
  if (context === undefined) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};
